using System; using System.Collections.Generic; using System.Linq; using System.Text; using System.Threading.Tasks;  namespace SensorVisualizer {     static class TimeCounter     {         private static DateTime startTime = DateTime.Now;         public static ulong Millis()         {             return (ulong)Math.Max(0L, DateTime.Now.Subtract(startTime).TotalMilliseconds);         }     } }